import React from 'react';
import { TouchableOpacity } from 'react-native';
import { View,StyleSheet,Dimensions,Text } from 'react-native';
import { MaterialCommunityIcons } from "../../node_modules/@expo/vector-icons";
import Constants from 'expo-constants';

function Buttons({first,second,third,fourth,onPress}) {
    return (
        <View style={styles.Container}>
            <TouchableOpacity onPress={()=>onPress(first.id)} activeOpacity="0.1">
                <View style={styles.buttons}>
                    <Text style={styles.text} >{first.name}</Text>
                </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={()=>onPress(second.id)} activeOpacity="0.8">    
                <View style={styles.buttons}>
                    <Text style={styles.text}>{second.name}</Text>
                </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={()=>onPress(third.id)} activeOpacity="0.8">
                <View style={styles.buttons}>
                    <Text style={styles.text}>{third.name}</Text>
                </View>
            </TouchableOpacity>
            <TouchableOpacity onPress={()=>onPress(fourth.id)} activeOpacity="0.8">
                <View style={styles.buttons}>
                    <Text style={styles.text}>{fourth.name}</Text>
                </View>
            </TouchableOpacity>
           
            
        </View>
    );
}

export default Buttons;
const styles = StyleSheet.create({
    Container:{
        flexDirection:"row",
        alignItems:"center",
        backgroundColor:'cyan',
        marginBottom:Dimensions.get('screen').height/40,
       
       
        
    },
    buttons:{
        backgroundColor:"black",
        
        width:Dimensions.get('screen').width/5,
        height:Dimensions.get('screen').height/10,
        marginHorizontal:Dimensions.get('screen').width/40,
        justifyContent:"center",
        alignContent:"center",
        alignItems:"center",
        borderColor:'royalblue',
        borderWidth:2,
    
        
    },
    text:{
        color:'dodgerblue',
        fontSize:35,
        fontFamily:Constants.platform.android?'notoserif':'Avenir Next',
    }
})
