import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import Display from './Components/TextInput/Display';
import Buttons from './Components/Buttons/Buttons';
import ClearNequal from './Components/Buttons/ClearNequal';
import Constants from 'expo-constants';
import { TouchableOpacity } from 'react-native';

export default function App() {
  const [value,setValue] = useState('0');
  const [prevOperation,setPrevOperation]=useState('');
  const [preValue,setPreValue] = useState('');
  const [anyOperationTouched,setAnyOperationTouched] = useState(false);
  const [isInt,setIsInt] = useState(false);


  const addDecimalPont = (number, operation, decimal) =>{
    let newResult = "";
    //console.log("Add decimal",number);
    //console.log("Decimals-",decimal);
    if(number.indexOf('.')!=-1){
      //newResult = number;
      if(number.indexOf('e')!=-1){
        const decimalPoints = number.slice(number.indexOf('+')+1);
        //console.log("Decimal points--",decimalPoints);
        newResult=number.slice(0,number.indexOf('e'));
        const shiftingDecimals = decimalPoints-(decimal*2);
        //console.log("Shifting decimals--",shiftingDecimals)
        const newFloatPoint = (newResult.indexOf('.')+shiftingDecimals);
        //console.log("New float point--",newFloatPoint);
        newResult=newResult.slice(0,newResult.indexOf('.'))+newResult.slice(newResult.indexOf('.')+1);
        //console.log("NEW RESULT FIRST RESULT--",newResult);
        newResult=newResult.slice(0,newFloatPoint)+"."+newResult.slice(newFloatPoint);
        //console.log("NEW RESULT SECOND RESULT--",newResult);
      }else{
        newResult = number;
      }
    }else{
      //let a = number.indexOf('e');
      //console.log("ADD decimal else part",number);
      if(number.indexOf('e')!=-1){
        //number=number.slice(0,number.indexOf('e'));
        //console.log("Number is",number);
      }
      if (operation == "+" || operation == "-") {
        if(decimal==0){
          newResult = number;
        }else{
          newResult = number.slice(0, (number.length - decimal)) + "." + number.slice((number.length - decimal));
        }

      } else if (operation == "*" || operation == "/") {
        if(decimal==0){
          newResult = number;
        }else{
          newResult = number.slice(0, number.length - (decimal * 2)) + "." + number.slice(number.length - (decimal * 2));
        }

      }if(newResult.charAt(0)=='.'){
        newResult = "0".concat(newResult);
      }

      if(newResult.includes('.')){
        while(true){
          if(newResult.charAt(newResult.length-1)!='0'){
            break
          }else{
            newResult = newResult.slice(0,(newResult.length)-1);
          }


        }
      }

      //console.log(newResult.charAt(newResult.length-1)); 
    }
    //console.log("newResult----",newResult);
    if(newResult.charAt(newResult.length-1)=='.'){
      //console.log("00000000000000000000000000000000000000")
      newResult = newResult.slice(0,(newResult.length)-1);
    }
    return newResult;
  }

  const removeDecimals = (number, decimalPointsInNumber, decimal) =>{
    for (let index = 0; index < decimal - decimalPointsInNumber; index++) {
      number = number.concat("0");

    }
    return number;
  }


  const getResult = (firstNumber,secondNumber,operation) =>{
    let firstNumDecimals = 0;
    let secondNumDecimals = 0;

    let result = 0;
    if (firstNumber.indexOf('.') != -1) {
        firstNumDecimals = (firstNumber.length - firstNumber.indexOf('.')) - 1;
    }
    if (secondNumber.indexOf('.') != -1) {
        secondNumDecimals = (secondNumber.length - secondNumber.indexOf('.')) - 1;
    }
    const decimals = firstNumDecimals > secondNumDecimals ? firstNumDecimals : secondNumDecimals;
    if (firstNumDecimals == decimals) {
        firstNumber = firstNumber.replace('.', '');
        secondNumber = removeDecimals(secondNumber.replace('.', ''), secondNumDecimals, decimals);
    } else if (secondNumDecimals == decimals) {
        secondNumber = secondNumber.replace('.', '');
        firstNumber = removeDecimals(firstNumber.replace('.', ''), firstNumDecimals, decimals);
    }
    //console.log("First number--",firstNumber,"  Second number--",secondNumber);
    if (operation == "+" || operation == "-") {
        if (operation == "+") {
            result = parseInt(firstNumber) + parseFloat(secondNumber);
        } else {
            result = parseInt(firstNumber) - parseFloat(secondNumber);
        }
    } else if (operation == "/" || operation == "*") {
        if (operation == "/") {
            result = parseInt(firstNumber) / parseFloat(secondNumber);
        } else {
            result = parseInt(firstNumber) * parseFloat(secondNumber);

        }


    }
    //console.log("Result-----",result);
   // console.log(result);
    //console.log(decimals);
    result =  addDecimalPont(result.toString(), operation, decimals);
    //console.log(result)
    //console.log('\n');
    return result;
  }


  const vals = (item) =>{
    if(item=='delete'){
      if(value.length==1){
        setValue('0');
      }else{
        const newData = value.slice(0,value.length-1);
        setValue(newData);
      }
    }else if(item=='+' || item=='-' || item=='/' || item=='*'){
     // console.log("Item--",item);
     // console.log("Begining the function")
     // console.log("Value(",value,")    PrevValue(",preValue,")  OP(",prevOperation,")");
     // console.log('\n','\n');
      setAnyOperationTouched(true);
      if(prevOperation.length>0){
        if(value.length>0 && preValue.length>0){

          let newValue = getResult(preValue,value,prevOperation);
          //console.log("=======================================",newValue);
          //console.log("Value(",value,")    PrevValue(",preValue,")");
          if(newValue.includes('.')){
            if(newValue.indexOf('.')>=15){
              setValue('Out of range');
              setPreValue('');
              setPrevOperation('');
              setAnyOperationTouched(false);
            }else{
              setValue(newValue);
              setPrevOperation(item);
              setPreValue('');
            }
          }else if(newValue.length>=16){
            setValue('Out of range');
            setPreValue('');
            setPrevOperation('');
            setAnyOperationTouched(false);
          }else{
            setValue(newValue);
            setPrevOperation(item);
            setPreValue('');
          }

        }else{
          setPrevOperation(item);
        }
        //console.log("Hello");
        //console.log("Value(",value,")    PrevValue(",preValue,")  OP(",prevOperation,")");

        /*if(prevOperation=='+'){
          let newValue = getResult(preValue,value,item);
          setValue(newValue.toString());
          setPrevOperation(item);
          setPreValue('');
        }*/
      }else{
        setPreValue(value);
        setPrevOperation(item);
        setValue('');
      }
    } 
    else{
      if(value.length==0){
        //console.log("value length is 0");
        //if nothing displays,display current input
        setValue(item);
        if(anyOperationTouched){
          setAnyOperationTouched(false);
        }
      }else if(value.length>0){
        //console.log("Inside value.length>0")
        //console.log("Value(",value,")    PrevValue(",preValue,")  OP(",prevOperation,")");
        //console.log('\n','\n');
        //console.log("value lenght is > 0");
        if(value.length==1 && value=='0'){
          if(item=='.'){
            //if displayed number equals 0 and the user input equals to '.' 
            const newData = value.concat(item);
            setValue(newData);
          }else{
            //if displayed number equals 0 then nothing changed
            setValue(item);
          }

        }else if(value.length==1 && value!='0'){

          if(anyOperationTouched==true){

            setPreValue(value);
            setValue(item);
            setAnyOperationTouched(false);
          }else{
            const newData = value.concat(item);
            setValue(newData);
          }


        }


        else{
          if(prevOperation.length>0){
            if(anyOperationTouched){
              setPreValue(value);
              setValue(item);
              setAnyOperationTouched(false);
            }else{
              const newData = value.concat(item);
              setValue(newData);
            }

          }else{
            const newData = value.concat(item);
            setValue(newData);
          }

        }

    }

    }



  }
  const resetData = () =>{
    setValue('0');
    setPreValue('');
    setPrevOperation('');
    setAnyOperationTouched(false);
  }

  //console.log("OUTSIDE THE FUNCTION")
  //console.log("Value(",value,")    PrevValue(",preValue,")  OP(",prevOperation,")");
  //console.log('\n','\n');


  return (
    <View style={styles.container}>
      <StatusBar style="light" />
      <View>
        <Display data={value}/>
      </View>
      <View style={styles.container2}>

        <Buttons first={{name:'numeric-7',id:"7"}} second={{name:'numeric-8',id:"8"}} third={{name:'numeric-9',id:"9"}} fourth={{name:'division',id:'/'}} onPress={vals}/>
        <Buttons first={{name:'numeric-4',id:"4"}} second={{name:'numeric-5',id:"5"}} third={{name:'numeric-6',id:"6"}} fourth={{name:'alpha-x',id:'*'}} onPress={vals}/>
        <Buttons first={{name:'numeric-1',id:"1"}} second={{name:'numeric-2',id:"2"}} third={{name:'numeric-3',id:"3"}} fourth={{name:'plus',id:'+'}} onPress={vals}/>
        <Buttons first={{name:'arrow-left-bold',id:'delete'}} second={{name:'numeric-0',id:"0"}} third={{name:'circle-small',id:'.'}} fourth={{name:'minus',id:'-'}} onPress={vals}/>
      </View>
      <View style={styles.container3}>
        <ClearNequal name="=" id="=" onPress={vals} />
      </View>
      <View style={styles.container3}>
        <ClearNequal name="Clear" id="clear" onPress={resetData} />
      </View>

    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection:"column",
    backgroundColor: 'black',
    paddingTop:Constants.statusBarHeight,
    alignItems:"center"

  },
  container3:{
    flexDirection:"column",
    width:"100%",
    borderRadius:25,
    margin:4,

    alignItems:"center",
    justifyContent:"center",
  },
  container2:{
    flex:1,
    backgroundColor:"black",
    width:"100%",

  },
  text:{
    fontWeight:"bold",
    fontSize:40
  }
})
